import React, { useState } from "react";
import { useFormik } from "formik";
import axios from "axios";
import { Form, Button, Col } from "react-bootstrap";
function Post() {
  const [values, setvalues] = useState([]);
  const url = "http://127.0.0.1:8000/api/person/";
  const formik = useFormik({
    initialValues: {
      name: "",
      lastname: "",
      age: "",
    },
    onSubmit: (values) => {
      axios.post(`${url}`).then((resp) => setvalues(resp.data));
      axios.get(`${url}`).then((resp) => setvalues(resp.data));
    },
  });
  const PersonLIst = values.map((item) => (
    <ul>
      <li>{item.name}</li>
    </ul>
  ));
  return (
    <>
      <Form onSubmit={formik.handleSubmit}>
        <Form.Row>
          <Col>
            <Form.Control
              type="text"
              placeholder="name"
              id="name"
              name="name"
              value={formik.values.name}
              onChange={formik.handleChange}
            />
          </Col>
          <Col>
            <Form.Control
              type="text"
              placeholder="lastname"
              id="lastname"
              name="lastname"
              value={formik.values.lastname}
              onChange={formik.handleChange}
            />
          </Col>
          <Col>
            <Form.Control
              type="text"
              placeholder="age"
              id="age"
              name="age"
              value={formik.values.age}
              onChange={formik.handleChange}
            />
          </Col>
          <Col>
            <Button type="submit">Add person</Button>
          </Col>
        </Form.Row>
      </Form>
      {PersonLIst}
    </>
  );
}

export default Post;
